// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title EHR_Certify - Hp ng chng thc 
 * @dev Contract ny qun l vic chng thc (ghi Hash) v cp quyn truy cp (phn quyn) 
 * cho H s Sc khe in t (Electronic Health Record - EHR).
 * @notice c s dng :
 * - Lu tr bng chng v s tn ti ca h s trn blockchain
 * - Qun l quyn truy cp ca bc s i vi h s
 * - m bo tnh ring t v bo mt ca thng tin y t
 */
contract EHR_Certify {
    // Bin lu tr a ch ca qun tr vin h thng (ngi trin khai contract)
    address public owner;

    // Constructor  khi to qun tr vin h thng
    constructor() {
        owner = msg.sender;
    }

    // 1. LU TR D LIU 
    // Cc bin trng thi c lu tr vnh vin trn blockchain

    // nh x: Hash ca h s (bytes32) -> a ch ca Bnh nhn (ch s hu)
    mapping(bytes32 => address) public recordOwner;

    // Thm struct cho quyn truy cp c thi hn
    struct AccessGrant {
        bool isValid;        // Trng thi hiu lc ca quyn
        uint256 expiryTime; // Thi im ht hn
        uint8 accessLevel;  // Cp  quyn truy cp
    }

    // nh x: Hash ca h s -> a ch Bc s -> Trng thi (c php/khng)
    // (bytes32 => (address => bool))
    mapping(bytes32 => mapping(address => AccessGrant)) public accessList;

    /**
     * @notice Lu tr danh sch bc s  c xc minh v c php tham gia h thng
     * @dev nh x a ch v ca bc s vi trng thi xc thc ca h
     * true:  c xc minh v c th tham gia h thng
     * false: Cha c xc minh hoc  b hy xc minh
     */
    mapping(address => bool) public isVerifiedDoctor;

    // 2. S KIN 
    // Thng bo khi c h s mi c ghi
    event RecordCertified(bytes32 indexed hash, address indexed owner);
    // Thng bo khi c quyn c cp
    event AccessGranted(bytes32 indexed hash, address indexed doctor, address indexed granter);
    /**
     * @dev S kin c pht ra khi c truy cp khn cp vo h s
     * @param hash Hash ca h s c truy cp
     * @param doctor a ch ca bc s thc hin truy cp khn cp
     * @param timestamp Thi im truy cp
     * @notice S kin ny gip:
     * - Ghi li mi ln truy cp khn cp
     * - Thng bo cho ch s hu h s
     * - To du vt kim ton cho mc ch theo di
     */
    event EmergencyAccess(
        bytes32 indexed hash,
        address indexed doctor,
        uint256 timestamp
    );


    // 3. CC HM CHC NNG (FUNCTIONS)

    /**
     * @dev Bnh nhn gi hm ny  ghi chng thc Hash ca h s.
     * @param _hash Hash SHA-256 (dng bytes32) ca file h s.
     */
    function recordHash(bytes32 _hash) public {
        // m bo Hash ny cha c ai ng k
        require(recordOwner[_hash] == address(0), "EHR: Hash already certified.");

        // Gn ngi gi (Bnh nhn) lm ch s hu
        recordOwner[_hash] = msg.sender;
        emit RecordCertified(_hash, msg.sender);
    }

    /**
     * @dev Bnh nhn (ch s hu) gi hm ny  cp quyn cho Bc s.
     * @param _hash Hash ca h s mun chia s.
     * @param _doctorAddress a ch v ca Bc s c cp quyn.
     */
    function grantAccess(bytes32 _hash, address _doctorAddress) public {
        // Ch ch s hu (Bnh nhn) mi c quyn cp php
        require(recordOwner[_hash] == msg.sender, "EHR: Only the owner can grant access.");
        // Bc s khng th t cp quyn cho mnh
        require(_doctorAddress != msg.sender, "EHR: Cannot grant access to self.");

        accessList[_hash][_doctorAddress] = AccessGrant({
            isValid: true,
            expiryTime: 0,
            accessLevel: 1
        });
        emit AccessGranted(_hash, _doctorAddress, msg.sender);
    }

    /**
     * @dev Bnh nhn (ch s hu) gi hm ny  cp quyn c thi hn cho Bc s.
     * @param _hash Hash ca h s mun chia s.
     * @param _doctorAddress a ch v ca Bc s c cp quyn.
     * @param _duration Thi gian cp quyn (tnh bng giy).
     * @param _accessLevel Cp  truy cp (1: c, 2: Ghi, 3: Qun tr).
     */
    function grantTimeBasedAccess(
        bytes32 _hash,
        address _doctorAddress,
        uint256 _duration,
        uint8 _accessLevel
    ) public {
        require(recordOwner[_hash] == msg.sender, "Only owner can grant access");
        require(_accessLevel >= 1 && _accessLevel <= 3, "Invalid access level");
        
        accessList[_hash][_doctorAddress] = AccessGrant({
            isValid: true,
            expiryTime: block.timestamp + _duration,
            accessLevel: _accessLevel
        });
    }

    /**
     * @dev Bt k ai cng c th gi hm ny  kim tra quyn truy cp (Hm View).
     * @param _hash Hash ca h s cn kim tra.
     * @param _userAddress a ch ca ngi dng (Bc s) cn kim tra.
     * @return bool Tr v true nu ngi dng c quyn, ngc li false.
     */
    function checkAccess(bytes32 _hash, address _userAddress) public view returns (bool) {
        // Kim tra xem ngi  c trong danh sch cp quyn khng
        return accessList[_hash][_userAddress].isValid;
    }

    /**
     * @dev Hm tin ch  xc minh tnh ton vn v s tn ti.
     * @param _hash Hash ca h s cn kim tra.
     * @return bool Tr v true nu Hash  tn ti trn chui.
     */
    function verifyRecord(bytes32 _hash) public view returns (bool) {
        return (recordOwner[_hash] != address(0));
    }

    /**
     * @dev Hm thu hi quyn truy cp ca Bc s i vi h s.
     * @param _hash Hash ca h s.
     * @param _doctorAddress a ch ca Bc s.
     */
    function revokeAccess(bytes32 _hash, address _doctorAddress) public {
        require(recordOwner[_hash] == msg.sender, "Only owner can revoke access");
        accessList[_hash][_doctorAddress].isValid = false;
    }

    /**
     * @dev Xc minh v cho php mt bc s tham gia vo h thng
     * @param _doctorAddress a ch v ca bc s cn xc minh
     * @notice Ch qun tr vin (owner) mi c quyn xc minh bc s. 
     * Quy trnh xc minh bc s bao gm:
     * 1. Kim tra giy php hnh ngh y
     * 2. Xc minh danh tnh v chuyn mn
     * 3. Cp quyn tham gia h thng
     * Sau khi c xc minh, bc s c th:
     * - c bnh nhn cp quyn xem h s
     * - S dng chc nng truy cp khn cp khi cn
     */
    function verifyDoctor(address _doctorAddress) public {
        require(msg.sender == owner, "Only system administrator can verify doctors");
        isVerifiedDoctor[_doctorAddress] = true;
    }

    /**
     * @dev Hy t cch tham gia h thng ca mt bc s
     * @param _doctorAddress a ch v ca bc s cn hy xc minh
     * @notice Ch qun tr vin (owner) mi c quyn hy xc minh bc s.
     * Vic hy xc minh c th c thc hin trong cc trng hp:
     * - Bc s vi phm quy nh ca h thng
     * - Giy php hnh ngh ht hn hoc b thu hi
     * - Bc s t nguyn ri khi h thng
     * Sau khi b hy xc minh, bc s s:
     * - Khng th s dng chc nng truy cp khn cp
     * - Cc quyn truy cp hin c vn gi nguyn (cn c thu hi ring nu mun)
     */
    function unverifyDoctor(address _doctorAddress) public {
        require(msg.sender == owner, "Only system administrator can unverify doctors");
        isVerifiedDoctor[_doctorAddress] = false;
    }

    /**
     * @dev Hm truy cp khn cp, cho php bc s  c xc minh truy cp h s trong trng hp cp cu
     * @param _hash Hash ca h s cn truy cp
     * @notice Chc nng ny c s dng trong cc trng hp:
     * - Cp cu y t khn cp
     * - Bnh nhn bt tnh hoc khng th t cp quyn
     * - Tnh hung nguy cp cn xem thng tin y t ngay lp tc
     * - Bnh nhn cha kp cp quyn nhng cn c iu tr gp
     * @notice  m bo tnh minh bch v trch nhim:
     * - Ch bc s  c xc minh mi c th s dng
     * - Mi ln truy cp khn cp u c ghi li
     * - Ch s hu h s s c thng bo
     * - Bc s phi chu trch nhim v vic s dng chc nng ny
     */
    function emergencyAccess(bytes32 _hash) public {
        // Kim tra xem ngi gi c phi l bc s  c xc minh
        require(isVerifiedDoctor[msg.sender], "Only verified doctors can use emergency access");
        
        // Ghi log v thng bo cho ch s hu v vic truy cp khn cp
        emit EmergencyAccess(_hash, msg.sender, block.timestamp);
    }
}

